&lt;script&gt;
    var myVideo = videojs(&#39;myVideo&#39;, &#123;
        bigPlayButton: true,
        textTrackDisplay: false,
        posterImage: false,
        errorDisplay: false,
    &#125;)
    myVideo.play()
    var changeVideo = function(vdoSrc) &#123;
        if (/\.m3u8$/.test(vdoSrc)) &#123;
            myVideo.src(&#123;
                src: vdoSrc,
                type: &#39;application/x-mpegURL&#39;
            &#125;)
        &#125; else &#123;
            myVideo.src(vdoSrc)
        &#125;
        myVideo.load();
        myVideo.play();
    &#125;
&lt;/script&gt;
